VAR
    running : BOOL := FALSE;
    counter : DINT := 0;
    reached : BOOL := FALSE;
    t20s    : TON;        // 20-s-Timer, läuft zyklisch
    y       : DINT := 0;  // Ergebnis
END_VAR
// Wichtig: Bei Job-Methoden deklariert man den Funktionsbaustein mit
//{attribute 'OPC.UA.DA.JobMethod' := 'NameDerUAMethod'}
